      <div>
        <h2>{props.name}</h2>
        <div className="summary">
          <span >{price}</span>
          <span >x {props.amount}</span>
        </div>
      </div>
      <div >
        <button onClick={props.onRemove}>âˆ’</button>
        <button onClick={props.onAdd}>+</button>
      </div>
    </li>
  );
};

export default CartItems;

